using namespace LW;

//////////////////////////////////////////////////////////////////////////////////////////
class GameAiClientHandler : public AbstractSocketSessionHanlder {
public:
	SocketSession* _session;
	GameAIMgr* _aimgr;

public:
	GameAiClientHandler(GameAIMgr* _aimgr) {
		this->_aimgr = _aimgr;
	}

	virtual ~GameAiClientHandler() {
	}

protected:
	virtual int onSocketConnected(SocketSession* session) override
	{
		if (!this->_aimgr->_is_init) {
			this->_aimgr->_lock.lock();
			if (!this->_aimgr->_is_init) {
				this->_aimgr->_is_init = true;

				this->_aimgr->_timer->start(100, 100,
					[this](int tid, unsigned int tms) -> bool {
					for (int i = 0; i < GAME_AI_COUNT; i++) {
						if (this->_aimgr->sessions[i] != NULL) {
							if (this->_aimgr->sessions[i]->connected()) {
								platform::msg_chat_request msg;
								msg.set_device(1);
								msg.set_from_uid(i);
								msg.set_to_uid(10000);
								//
								msg.set_msg("{\"name\":\"liwei\",\"age\":30,\"sex\":1,\"address\":\"\"}");
								int c = msg.ByteSizeLong();
								std::unique_ptr<char[]> s(new char[c]());
								bool ret = msg.SerializeToArray(s.get(), c);
								if (ret)
								{
									this->_aimgr->sessions[i]->sendData(cmd_platform_cs_chat_request, s.get(), c);
								}
							}
						}
					}
					return true;
				});
			}

			this->_aimgr->_lock.unlock();
		}

		this->_session = session;

		return 0;
	}

	virtual int onSocketDisConnect(SocketSession* session) override
	{
		return 0;
	}

	virtual int onSocketTimeout(SocketSession* session) override
	{
		return 0;
	}

	virtual int onSocketError(SocketSession* session) override
	{
		return 0;
	}

protected:
	virtual void onSocketParse(SocketSession* session, lw_int32 cmd,
		lw_char8* buf, lw_int32 bufsize) override
	{
		switch (cmd) {
		case cmd_connected: {
			platform::msg_connected msg;
			msg.ParseFromArray(buf, bufsize);
			LOGFMTD("client_id[%d], connected. [time : %d]", msg.client_id(), msg.time());

			break;
		}
		case cmd_heart_beat: {
			platform::msg_heartbeat msg;
			msg.ParseFromArray(buf, bufsize);
			LOGFMTD("heartBeat[%d]", msg.time());

			break;
		}
		case cmd_platform_sc_userinfo: {
			platform::msg_userinfo_reponse msg;
			msg.ParseFromArray(buf, bufsize);
			LOGFMTD("userid: %d name:%s", msg.uid(), msg.name().c_str());

			break;
		}
		case cmd_platform_sc_chat_reponse: {
			platform::msg_chat_reponse msg;
			msg.ParseFromArray(buf, bufsize);
			LOGFMTD("from_uid: %d to_uid:%d msg:%s", msg.from_uid(), msg.to_uid(), msg.msg().c_str());

			break;
		}
		default: {

			break;
		}
		}
	}
};

//////////////////////////////////////////////////////////////////////////////////////////
GameAIMgr::GameAIMgr() {
	this->_is_init = false;
	this->_processor = new SocketProcessor;
	this->_timer = new Timer;
}

GameAIMgr::~GameAIMgr() {
	delete this->_processor;
	delete this->_timer;
}

int GameAIMgr::onStart() {
	bool ret = this->_processor->create(false);
	if (ret) {
		this->_timer->create(this->_processor);
		for (int i = 0; i < GAME_AI_COUNT; i++) {
			SocketSession *session = new SocketSession(new GameAiClientHandler(this),
				new SocketConfig("127.0.0.1", 19801));
			int r = session->create(SESSION_TYPE::Client, _processor);
			if (r == 0) {
				sessions[i] = session;
			}
			else {
				delete session;
			}
		}
	}

	return 0;
}

int GameAIMgr::onRun() {

	int ret = this->_processor->dispatch();

	return 0;
}

int GameAIMgr::onEnd() {
	return 0;
}


GameAIMgr* __g_ai_mgr;
int main_ai_server(int argc, char** argv) {
	
	__g_ai_mgr = new GameAIMgr;
	__g_ai_mgr->start();

	while (1)
	{
		lw_sleep(1);
	}

	return 0;
}