lw_fast_lock __g_lock;

static bool is_init = false;

using namespace LW;

GameServer* __g_server[1024] = {0};

class GameAITestTimer : public Threadable
{
	SocketProcessor _processor;
	Timer _timer;

public:
	GameAITestTimer() {

	}

	~GameAITestTimer() {

	}

protected:
	virtual int onStart() {
		_processor.create(true, NULL);
		_timer.create(&_processor);
		_timer.start(100, 1000, [](int tid, unsigned int tms) -> bool
		{
			platform::msg_chat_request msg;
			msg.set_device(1);
			msg.set_from_uid(10000);
			msg.set_to_uid(20000);
			msg.set_msg("{\"name\":\"liwei\",\"age\":30,\"sex\":1,\"address\":\"\"}");
			int c = msg.ByteSizeLong();
			std::unique_ptr<char[]> s(new char[c]());
			bool ret = msg.SerializeToArray(s.get(), c);
			if (ret)
			{
				for (int i = 0; i < 1024; i++) {
					if (__g_server[i] != NULL) {
						if (__g_server[i]->client->getSession()->connected()) {
							__g_server[i]->sendData(cmd_platform_cs_chat_request, s.get(), c);
						}
					}
				}	
			}
			return true;
		});
		return 0;
	}

	virtual int onRun() {
		_processor.dispatch();
		return 0;
	}

	virtual int onEnd() {
		return 0;
	}
};

